package org.assignment.datastore.filedatastore.util;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import org.assignment.datastore.model.DataStoreKey;

import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.time.LocalDateTime;
import java.time.ZoneId;

public class FileHandlerUtil {

    public static ObjectMapper mapper=new ObjectMapper();
    public static String defaultEmpty=
"                                 ~               ~";

    public static Integer getObjectSizeInKB(Object object) throws JsonProcessingException {
        return mapper.writeValueAsString(object).getBytes().length/1024;
    }

    public static byte[] getOutput(DataStoreKey key,Object value) throws JsonProcessingException {
        long expiryTime= LocalDateTime.now().plusMinutes(key.getTimeToLive()).atZone(ZoneId.systemDefault()).toInstant().toEpochMilli();
        return (getOutputKey(key.getKey())+"~"+getExpiryTime(String.valueOf(expiryTime))+"~"+getObjectData(mapper.writeValueAsString(value))).getBytes();
    }

    private static String getOutputKey(String key){
        int length=key.length();
        for(int i=length;i<=32;i++){
            key=key+" ";
        }
        return key;
    }

    private static String getExpiryTime(String key){
        int length=key.length();
        for(int i=length;i<=14;i++){
            key=key+" ";
        }
        return key;
    }
    private static String getObjectData(String value){
        int length=value.length();
        for(int i=length;i<=16384;i++){
            value=value+" ";
        }
        return value;
    }


    public static  boolean isKeyExpired(String line) {
        Long expiryTime=Long.parseLong(line.split("~")[1].trim());
        Long currentTime= System.currentTimeMillis();
        return currentTime>=expiryTime;
    }


}
